mapper.update:
update granted_holiday_log
set employee_id = #{employeeId,jdbcType=INTEGER},
  status_code = #{statusCode,jdbcType=VARCHAR},
  days = #{days,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select ghl.id, e.employee_id as employee_code, st.value as status, ghl.days, ghl.create_time from granted_holiday_log ghl
left join employee e on e.id = ghl.employee_id
left join master_class st on st.code = ghl.status_code and st.type = 'PROC'
where ghl.employee_id = #{employeeId,jdbcType=BIGINT}
order by create_time asc limit #{page},#{size}
